import React, {useState} from "react";
// import PropTypes from 'prop-types'
import { Link, useParams } from "react-router-dom";
import Markdown, {compiler} from 'markdown-to-jsx'

const News = (props) => {
  const [markDown, setMarkDown] = useState("# Loading article...");

  let { year, month, day, name } = useParams();

  let md = fetch(`/posts/${year}-${month}-${day}-${name}.md`).then(res => res.text()).then(t => t).catch(e => e);


  return (
    <div>
      <div className="containerX">
        <div className="row">
          <div className="col-xs-12 col-md-8 col-lg-7">
            <Link to="/archive">Back to archive</Link>
            <h2>
              {year}/{month}/{day} {name}
            </h2>
            {/* <Markdown children={md}/> 
            */}
            {compiler(md)}
          </div>
          <article dangerouslySetInnerHTML={{__html: markdown}}></article>

        </div>
      </div>
    </div>
  );
};

News.propTypes = {};

export default News;
